mapper.select:

    		select   no,
	        		 name,
	        		 gender
			from users
			where id = #{id}
			and password = #{password}

mapper.update:

    		update  users
    		set 	id = #{id},
        		    password = #{password},
        			name = #{name},
        			gender = #{gender}
			where no = #{no}

mapper.select:

    		select id
			from users
			where id = #{id}
